function Airtable({tableName, callback}) {
  var Airtable = require('airtable');
  const api_base = new Airtable({apiKey: 'keyBCQBasy6oWdP63'}).base('app3H9oIfok6KQvG5');

  const fetchData = (tableName, callback) => {
    var fetchedRecords = [];
    api_base(tableName).select().eachPage(function page(records, fetchNextPage) {
      // This function (`page`) will get called for each page of records.
      records.forEach(function(record) {
        fetchedRecords.push(record);
      });

      // To fetch the next page of records, call `fetchNextPage`.
      // If there are more records, `page` will get called again.
      // If there are no more records, `done` will get called.
      fetchNextPage();

    }, function done(err) {
      if (err) { console.error(err); return; }
      callback(fetchedRecords);
    });
  }
}

export default Airtable;
